// generated by gong
import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { GanttDB } from '../gantt-db'
import { GanttService } from '../gantt.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-gantt-sorting',
  templateUrl: './gantt-sorting.component.html',
  styleUrls: ['./gantt-sorting.component.css']
})
export class GanttSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of Gantt instances that are in the association
  associatedGantts = new Array<GanttDB>();

  constructor(
    private ganttService: GanttService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of gantt instances
    public dialogRef: MatDialogRef<GanttSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getGantts()
  }

  getGantts(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let gantt of this.frontRepo.Gantts_array) {
          let ID = this.dialogData.ID
          let revPointerID = gantt[this.dialogData.ReversePointer]
          let revPointerID_Index = gantt[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedGantts.push(gantt)
          }
        }

        // sort associated gantt according to order
        this.associatedGantts.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });

        console.log("front repo pull returned")
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedGantts, event.previousIndex, event.currentIndex);

    // set the order of Gantt instances
    let index = 0
    
    for (let gantt of this.associatedGantts) {
      let revPointerID_Index = gantt[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
    console.log("after drop")
  }

  save() {

    this.associatedGantts.forEach(
      gantt => {
        this.ganttService.updateGantt(gantt)
          .subscribe(gantt => {
            this.ganttService.GanttServiceChanged.next("update")
            console.log("gantt saved")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
