// generated by gong
import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { LaneDB } from '../lane-db'
import { LaneService } from '../lane.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-lane-sorting',
  templateUrl: './lane-sorting.component.html',
  styleUrls: ['./lane-sorting.component.css']
})
export class LaneSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of Lane instances that are in the association
  associatedLanes = new Array<LaneDB>();

  constructor(
    private laneService: LaneService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of lane instances
    public dialogRef: MatDialogRef<LaneSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getLanes()
  }

  getLanes(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let lane of this.frontRepo.Lanes_array) {
          let ID = this.dialogData.ID
          let revPointerID = lane[this.dialogData.ReversePointer]
          let revPointerID_Index = lane[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedLanes.push(lane)
          }
        }

        // sort associated lane according to order
        this.associatedLanes.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedLanes, event.previousIndex, event.currentIndex);

    // set the order of Lane instances
    let index = 0
    
    for (let lane of this.associatedLanes) {
      let revPointerID_Index = lane[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedLanes.forEach(
      lane => {
        this.laneService.updateLane(lane)
          .subscribe(lane => {
            this.laneService.LaneServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
