import { Component, OnInit, Inject, Optional } from '@angular/core';
import { TypeofExpr } from '@angular/compiler';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { MilestoneDB } from '../milestone-db'
import { MilestoneService } from '../milestone.service'

import { FrontRepoService, FrontRepo } from '../front-repo.service'
import { NullInt64 } from '../null-int64'

@Component({
  selector: 'lib-milestone-sorting',
  templateUrl: './milestone-sorting.component.html',
  styleUrls: ['./milestone-sorting.component.css']
})
export class MilestoneSortingComponent implements OnInit {

  frontRepo: FrontRepo = new (FrontRepo)

  // array of Milestone instances that are in the association
  associatedMilestones = new Array<MilestoneDB>();

  constructor(
    private milestoneService: MilestoneService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of milestone instances
    public dialogRef: MatDialogRef<MilestoneSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getMilestones()
  }

  getMilestones(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let milestone of this.frontRepo.Milestones_array) {
          let ID = this.dialogData.ID
          let revPointerID = milestone[this.dialogData.ReversePointer as keyof MilestoneDB] as unknown as NullInt64
          let revPointerID_Index = milestone[this.dialogData.ReversePointer + "_Index" as keyof MilestoneDB] as unknown as NullInt64
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedMilestones.push(milestone)
          }
        }

        // sort associated milestone according to order
        this.associatedMilestones.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer + "_Index" as keyof typeof t1] as unknown as NullInt64
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer + "_Index" as keyof typeof t2] as unknown as NullInt64
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }
          }
          return 0;
        });
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedMilestones, event.previousIndex, event.currentIndex);

    // set the order of Milestone instances
    let index = 0

    for (let milestone of this.associatedMilestones) {
      let revPointerID_Index = milestone[this.dialogData.ReversePointer + "_Index" as keyof MilestoneDB] as unknown as NullInt64
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
  }

  save() {

    this.associatedMilestones.forEach(
      milestone => {
        this.milestoneService.updateMilestone(milestone)
          .subscribe(milestone => {
            this.milestoneService.MilestoneServiceChanged.next("update")
          });
      }
    )

    this.dialogRef.close('Sorting of ' + this.dialogData.ReversePointer +' done');
  }
}
